INSERT INTO `el_single` VALUES ('1', '2', '注册登录', '<p>\r\n	1.用户可以通过手机/邮箱进行注册\r\n</p>\r\n<p>\r\n	2.点击发送验证码\r\n</p>\r\n<p>\r\n	3.输入验证码完成注册并完善资料\r\n</p>', '', '0', '0');
INSERT INTO `el_single` VALUES ('2', '2', '购买流程', '<p>\r\n	1.确保自己的钱包有足够的金币支付课程\r\n</p>\r\n<p>\r\n	2.进入课程详情\r\n</p>\r\n<p>\r\n	3.点击购买课程\r\n</p>\r\n<p>\r\n	4.确认支付\r\n</p>', '', '0', '0');
INSERT INTO `el_single` VALUES ('3', '2', '常见问题', '<p class=\"MsoNormal\" align=\"left\" style=\"vertical-align:middle;color:#666666;font-family:\'microsoft yahei\';background:white;\">\r\n	1.如何绑定、验证手机号？<span></span>\r\n</p>\r\n<p class=\"MsoNormal\" align=\"left\" style=\"vertical-align:middle;color:#666666;font-family:\'microsoft yahei\';background:white;\">\r\n	登录个人管理中心，在“管理中心<span>-账户设置</span><span>-</span>手机号码”里，可以完成手机验证。<span></span>\r\n</p>\r\n<p class=\"MsoNormal\" align=\"left\" style=\"vertical-align:middle;color:#666666;font-family:\'microsoft yahei\';background:white;\">\r\n	点击“立即验证”，即可进行手机验证<span></span>\r\n</p>\r\n<p class=\"MsoNormal\" align=\"left\" style=\"vertical-align:middle;color:#666666;font-family:\'microsoft yahei\';background:#FBFBFB;\">\r\n	2.密码忘记了怎么办？\r\n</p>\r\n<p class=\"MsoNormal\" align=\"left\" style=\"vertical-align:middle;color:#666666;font-family:\'microsoft yahei\';background:#FBFBFB;\">\r\n	点击右上角<span>“</span>登录<span>”</span>按钮，输入账号后，点击“忘记密码”即可完成密码找回。<span></span>\r\n</p>\r\n<p class=\"MsoNormal\" align=\"left\" style=\"vertical-align:middle;color:#666666;font-family:\'microsoft yahei\';background:#FBFBFB;\">\r\n	3.如何修改登录密码<span></span>\r\n</p>\r\n<p class=\"MsoNormal\" align=\"left\" style=\"vertical-align:middle;color:#666666;font-family:\'microsoft yahei\';background:white;\">\r\n	在“管理中心-账户设置<span>-</span>修改密码”里，输入原密码，输入新密码并确认新密码即可完成密码修改。\r\n</p>\r\n<p class=\"MsoNormal\" align=\"left\" style=\"vertical-align:middle;color:#666666;font-family:\'microsoft yahei\';background:#FBFBFB;\">\r\n	4.为什么要提交身份证信息？<span></span>\r\n</p>\r\n<p class=\"MsoNormal\" align=\"left\" style=\"vertical-align:middle;color:#666666;font-family:\'microsoft yahei\';background:#FBFBFB;\">\r\n	为保证学员顺利完成在线学习课程，确保考试证书的信息真实和对应，需进行身份信息真实性核验，所以需要上传身份证信息。\r\n</p>\r\n<div>\r\n	<br />\r\n</div>', '', '0', '0');
INSERT INTO `el_single` VALUES ('4', '2', '认证介绍', '<p>\r\n	1.普通用户可以申请教师资格认证\r\n</p>\r\n<p>\r\n	2.提交申请认证所需要的材料\r\n</p>\r\n<p>\r\n	3.需要1-2个工作日审核\r\n</p>\r\n<p>\r\n	4.审核通过后可以教师身份发布课程或进行授课\r\n</p>', '', '0', '0');
INSERT INTO `el_single` VALUES ('5', '3', '充值方式', '<p>\r\n	1.可通过支付宝和银行卡对账户进行充值\r\n</p>\r\n<p>\r\n	2.1学币=1RMB\r\n</p>\r\n<p>\r\n	3.请仔细阅读《Eduline虚拟货币服务协议》\r\n</p>', '', '0', '0');
INSERT INTO `el_single` VALUES ('6', '3', '支付说明', '<p>\r\n	1.对课程进行购买之后需要进行支付操作，请确认是否需要购买课程\r\n</p>\r\n<p>\r\n	2.在\"管理中心-财富管理-余额\"中可以对余额进行查看\r\n</p>\r\n<p>\r\n	3.在\"管理中心-财富管理-绑定银行卡\"中可以绑定自己的银行卡\r\n</p>', '', '0', '0');
INSERT INTO `el_single` VALUES ('7', '1', '关于公司', '<p>\r\n	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp; 成都赛新科技有限公司成立于2015年，是一家专业从事在线教育的新型互联网公司。<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;赛新科技专注于在线教育领域的系统研发和技术服务，为各大互联网教育机构和企业提供优质的在线教育系统。\r\n</p>\r\n<p>\r\n	<span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;其团队核心成员均拥有多年丰富的在线教育行业从业经验和海外教育背景，在历经一年多的产品研发和升级迭代，最终发布了目前的Eduline在线教育系统。</span><br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;同时，赛新科技正在致力成为一家社会企业，运用自身优势力量发起不同的公益活动并持续与不同的中小互联网公司分享经验、相互扶持，持续地履行我们的社会责任。<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;其可靠的技术保障、良好的售后服务和灵活的问题处理办法，让赛新科技成为您值得信赖的伙伴。 &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;我们的使命是为互联网创造更优秀的模式，为企业创造持续的价值，基于团队丰富的经验创造卓越的产品平台服务社会。<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;我们只做在线教育，所有我们专业！<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;我们追求极致，提供顶级的在线教育解决方案；我们追求创新，不断创造在线教育新模式！<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;我们致力于中国在线教育事业加速度，为中国的教育发展做出贡献！\r\n</p>\r\n<p>\r\n	<br />\r\n</p>', '', '0', '0');
INSERT INTO `el_single` VALUES ('8', '1', '关于团队', '<p>\r\n	<strong>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;我们只做在线教育，我们提供整套顶级的在线教育解决方案。</strong> \r\n</p>\r\n<p>\r\n	<strong>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;我们充满活力，我们追求极致，我们不断创新，我们热爱互联网、热爱在线教育！</strong> \r\n</p>\r\n<p>\r\n	<br />\r\n</p>', '', '0', '0');
INSERT INTO `el_single` VALUES ('9', '1', '关于产品', '<p>\r\n	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp; Eduline在线教育系统，为互联网教育机构和企业提供整套在线教育解决方案，其核心功能包括在线直播、视频录播教学、问答系统、即时聊天、小组交流、在线考试等，能满足不同行业的在线教育需求。更有Ios和Android移动端，让您轻松搭建在线教育平台。\r\n</p>\r\n<p>\r\n	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp; 转型互联网+教育，Eduline在线教育系统是您的最佳选择！\r\n</p>', '', '0', '0');
INSERT INTO `el_single` VALUES ('10', '4', '意见反馈', '<p>\r\n	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;如您有任何问题或建议，请发送邮件至wangjun@seition.com\r\n</p>\r\n<p>\r\n	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;欢迎吐槽\r\n</p>', '', '0', '0');
INSERT INTO `el_single` VALUES ('11', '4', '加入我们', '&nbsp;&nbsp;&nbsp;&nbsp;我们诚心邀请广大志同道合的朋友加入我们，以非凡的创新、创想能力，谋天下之智慧，释放出超越时代的能量。&nbsp;&nbsp;&nbsp;&nbsp;——赛新科技', '', '0', '0');
