INSERT INTO `el_zy_video` VALUES ('214', '1', '《恋爱心理学》', '434', ',434,', '教学目标：帮助同学深刻理解爱情的真谛，树立正确的、健康的爱情观；学习两性优质沟通态度及情绪管理技巧，处理好学业与爱情的关系；学习更为成熟的情感处理态度与技巧，学会自我保护；正确对待爱情挫折并战胜挫折；培养大学生处理情感问题的综合能力。\n教学任务：传授基本的爱情理论知识，使大学生形成正确、健康向上的爱情观；在理论的指导下，教给大学生处理爱情问题的基本技能与技巧，使大学生形成并提高爱与被爱的能力；培养大学生爱的审美能力；帮助大学生科学的恋爱心理卫生，促进大学生身心情感健康发展，并学会自我保护，从而确立正确的婚恋观。\n教学要求：以理论学习为主，注重理论与实际的结合；适当介绍该学科的发展动态与理论前沿。', 'http://chuyou.qiniudn.com/chuyou81416214988', '257', '32', '0', 'php', ',142,', '1', '50.00', '50.00', '0.00', '0', '0', '0', '1.00', 'chuyou81416214988', '0', '0', '1', '0', '0', '0', '1', '0', '0', '1416215007', '1510909409', '1416215243', '1458095339');
