  `feed_email_time` int(11) DEFAULT NULL,
  `send_email_time` int(11) DEFAULT NULL,
  `my_college` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '所属院校专业',
  `signup_college` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '报考院校专业',
  `study_phase` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '学习项目与阶段',
  `my_study_level` tinyint(3) unsigned NOT NULL DEFAULT '0' COMMENT '我的(0学生,1学神,2学霸,3学渣,4学弱)',
  `find_study_level` tinyint(3) NOT NULL DEFAULT '0' COMMENT '找(0学生,1学神,2学霸,3学渣,4学弱)',
  `mail_activate` tinyint(3) unsigned NOT NULL DEFAULT '0' COMMENT '邮箱激活状态',
  `phone_activate` tinyint(3) unsigned NOT NULL DEFAULT '0' COMMENT '手机激活状态',
  PRIMARY KEY (`uid`),
  UNIQUE KEY `email` (`email`),
  KEY `login` (`login`),
  KEY `uname` (`uname`)
) ENGINE=MyISAM AUTO_INCREMENT=185 DEFAULT CHARSET=utf8;

